def cel_to_K(x):    # Convert Celsius to Kelvin    return x + 273.15def K_to_cel(x):    # Convert Kelvin to Celsius    return x - 273.15def rem_to_sv(x):    # Convert Rem to Sievert    return x / 100def sv_to_rem(x):    # Convert Sievert to Rem    return x * 100def curie_to_bq(x):    # Convert Curie to Becquerel    return x * 3.7E10def bq_to_curie(x):    # Convert Becquerel to Curie    return x / 3.7E10def seconds_to_minutes(x):    # Convert seconds to minutes    return x / 60def seconds_to_hours(x):    # Convert seconds to hours    return x / (60*60)def seconds_to_days(x):    # Convert seconds to days    return x / (24*60*60)def seconds_to_years(x):    # Convert seconds to years    return x / (365.25*24*60*60)def years_to_days(x):    # Convert years to days    return x * (365.25)def years_to_hours(x):    # Convert years to hours    return x * (365.25*24)def years_to_minutes(x):    # Convert years to minutes    return x * (365.25*24*60)def years_to_seconds(x):    # Convert years to seconds    return x * (365.25*24*60*60)